import React, { useState, useEffect, useCallback } from 'react'
import {
	Box,
	TextField,
	Typography,
	FormControl,
	InputLabel,
	Select,
	MenuItem,
	Paper,
} from '@mui/material'
import { VehicleCategory, LicensePlateData } from '../types'

interface LicensePlateInputProps {
	value?: LicensePlateData
	onChange: (data: LicensePlateData) => void
	error?: string
	required?: boolean
}

// Разрешенные кириллические буквы для номеров согласно ГОСТ
const ALLOWED_LETTERS = 'АВЕКМНОРСТУХ'

// Категории транспортных средств
const VEHICLE_CATEGORIES = [
	{ value: 'standard', label: 'Обычные автомобили/грузовые/автобусы' },
	{ value: 'trailer', label: 'Прицепы/полуприцепы' },
	{ value: 'tractor', label: 'Тракторы, самоходные машины (Гостехнадзор)' },
	{ value: 'motorcycle', label: 'Мотоциклы/мототехника' },
] as const

const LicensePlateInput: React.FC<LicensePlateInputProps> = ({
	value,
	onChange,
	error,
	required = false,
}) => {
	const [plateData, setPlateData] = useState<LicensePlateData>(
		value || { category: 'standard' }
	)

	// Обновляем локальное состояние при изменении внешнего значения
	useEffect(() => {
		if (value) {
			setPlateData(value)
		}
	}, [value])

	// Функция для фильтрации ввода букв
	const filterLetters = useCallback((input: string): string => {
		return input
			.toUpperCase()
			.split('')
			.filter((char) => ALLOWED_LETTERS.includes(char))
			.join('')
	}, [])

	// Функция для фильтрации ввода цифр
	const filterDigits = useCallback((input: string): string => {
		return input.replace(/\D/g, '')
	}, [])

	// Обновление данных с валидацией
	const updatePlateData = useCallback(
		(updates: Partial<LicensePlateData>) => {
			const newData = { ...plateData, ...updates }
			setPlateData(newData)
			onChange(newData)
		},
		[plateData, onChange]
	)

	// Обработчик изменения категории
	const handleCategoryChange = useCallback(
		(category: VehicleCategory) => {
			// Очищаем все поля при смене категории
			const newData: LicensePlateData = { category }
			setPlateData(newData)
			onChange(newData)
		},
		[onChange]
	)

	// Функция для генерации итогового номера в текстовом формате
	const generatePlateNumber = useCallback((data: LicensePlateData): string => {
		switch (data.category) {
			case 'standard':
				if (data.letter1 && data.digits && data.letters && data.region) {
					return `${data.letter1}${data.digits}${data.letters} ${data.region}`
				}
				break
			case 'trailer':
				if (data.trailerLetters && data.trailerDigits && data.region) {
					return `${data.trailerLetters}${data.trailerDigits} ${data.region}`
				}
				break
			case 'tractor':
			case 'motorcycle':
				if (data.topDigits && data.bottomLetters && data.bottomRegion) {
					return `${data.topDigits} ${data.bottomLetters} ${data.bottomRegion}`
				}
				break
		}
		return ''
	}, [])

	// Рендеринг формы для стандартных автомобилей (L NNN LL | RR(R))
	const renderStandardForm = () => (
		<Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', alignItems: 'center' }}>
			<Paper
				elevation={1}
				sx={{
					display: 'flex',
					border: error ? '2px solid #d32f2f' : '2px solid #000',
					borderRadius: '8px',
					overflow: 'hidden',
					backgroundColor: '#fff',
				}}
			>
				{/* Первая буква */}
				<TextField
					value={plateData.letter1 || ''}
					onChange={(e) => {
						const filtered = filterLetters(e.target.value).slice(0, 1)
						updatePlateData({ letter1: filtered })
					}}
					variant="standard"
					inputProps={{
						style: {
							fontSize: '24px',
							fontWeight: 'bold',
							textAlign: 'center',
							width: '30px',
							border: 'none',
							outline: 'none',
						},
						placeholder: 'А',
						maxLength: 1,
					}}
					sx={{
						'& .MuiInput-root': {
							'&:before': { borderBottom: 'none' },
							'&:after': { borderBottom: 'none' },
							'&:hover:not(.Mui-disabled):before': {
								borderBottom: 'none',
							},
						},
						px: 1,
					}}
				/>

				{/* Три цифры */}
				<TextField
					value={plateData.digits || ''}
					onChange={(e) => {
						const filtered = filterDigits(e.target.value).slice(0, 3)
						updatePlateData({ digits: filtered })
					}}
					variant="standard"
					inputProps={{
						style: {
							fontSize: '24px',
							fontWeight: 'bold',
							textAlign: 'center',
							width: '60px',
							border: 'none',
							outline: 'none',
						},
						placeholder: '123',
						maxLength: 3,
					}}
					sx={{
						'& .MuiInput-root': {
							'&:before': { borderBottom: 'none' },
							'&:after': { borderBottom: 'none' },
							'&:hover:not(.Mui-disabled):before': {
								borderBottom: 'none',
							},
						},
						px: 1,
					}}
				/>

				{/* Две буквы */}
				<TextField
					value={plateData.letters || ''}
					onChange={(e) => {
						const filtered = filterLetters(e.target.value).slice(0, 2)
						updatePlateData({ letters: filtered })
					}}
					variant="standard"
					inputProps={{
						style: {
							fontSize: '24px',
							fontWeight: 'bold',
							textAlign: 'center',
							width: '50px',
							border: 'none',
							outline: 'none',
						},
						placeholder: 'ВС',
						maxLength: 2,
					}}
					sx={{
						'& .MuiInput-root': {
							'&:before': { borderBottom: 'none' },
							'&:after': { borderBottom: 'none' },
							'&:hover:not(.Mui-disabled):before': {
								borderBottom: 'none',
							},
						},
						px: 1,
						borderRight: '2px solid #000',
					}}
				/>

				{/* Регион */}
				<Box
					sx={{
						display: 'flex',
						flexDirection: 'column',
						alignItems: 'center',
						px: 1.5,
						py: 1,
						backgroundColor: '#fff',
						minWidth: '70px',
					}}
				>
					<TextField
						value={plateData.region || ''}
						onChange={(e) => {
							const filtered = filterDigits(e.target.value)
							if (filtered.length > 0 && filtered.replace(/0/g, '').length === 0) {
								return // Не позволяем вводить только нули
							}
							const region = filtered.slice(0, 3)
							updatePlateData({ region })
						}}
						variant="standard"
						inputProps={{
							style: {
								fontSize: '16px',
								fontWeight: 'bold',
								textAlign: 'center',
								width: '40px',
								border: 'none',
								outline: 'none',
							},
							placeholder: '77',
							maxLength: 3,
						}}
						sx={{
							'& .MuiInput-root': {
								'&:before': { borderBottom: 'none' },
								'&:after': { borderBottom: 'none' },
								'&:hover:not(.Mui-disabled):before': {
									borderBottom: 'none',
								},
							},
						}}
					/>
					<Box
						sx={{
							display: 'flex',
							alignItems: 'center',
							fontSize: '10px',
							fontWeight: 'bold',
							color: '#000',
						}}
					>
						RUS
						<Box
							sx={{
								ml: 0.5,
								width: '12px',
								height: '8px',
								background:
									'linear-gradient(to bottom, #fff 0%, #fff 33%, #0052cc 33%, #0052cc 66%, #d32f2f 66%, #d32f2f 100%)',
								border: '0.5px solid #000',
							}}
						/>
					</Box>
				</Box>
			</Paper>
			<Typography variant="body2" color="text.secondary">
				Пример: А123ВС 77
			</Typography>
		</Box>
	)

	// Рендеринг формы для прицепов (LL NNNN | RR(R))
	const renderTrailerForm = () => (
		<Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', alignItems: 'center' }}>
			<Paper
				elevation={1}
				sx={{
					display: 'flex',
					border: error ? '2px solid #d32f2f' : '2px solid #000',
					borderRadius: '8px',
					overflow: 'hidden',
					backgroundColor: '#fff',
				}}
			>
				{/* Две буквы */}
				<TextField
					value={plateData.trailerLetters || ''}
					onChange={(e) => {
						const filtered = filterLetters(e.target.value).slice(0, 2)
						updatePlateData({ trailerLetters: filtered })
					}}
					variant="standard"
					inputProps={{
						style: {
							fontSize: '24px',
							fontWeight: 'bold',
							textAlign: 'center',
							width: '50px',
							border: 'none',
							outline: 'none',
						},
						placeholder: 'ММ',
						maxLength: 2,
					}}
					sx={{
						'& .MuiInput-root': {
							'&:before': { borderBottom: 'none' },
							'&:after': { borderBottom: 'none' },
							'&:hover:not(.Mui-disabled):before': {
								borderBottom: 'none',
							},
						},
						px: 1,
					}}
				/>

				{/* Четыре цифры */}
				<TextField
					value={plateData.trailerDigits || ''}
					onChange={(e) => {
						const filtered = filterDigits(e.target.value).slice(0, 4)
						updatePlateData({ trailerDigits: filtered })
					}}
					variant="standard"
					inputProps={{
						style: {
							fontSize: '24px',
							fontWeight: 'bold',
							textAlign: 'center',
							width: '80px',
							border: 'none',
							outline: 'none',
						},
						placeholder: '9768',
						maxLength: 4,
					}}
					sx={{
						'& .MuiInput-root': {
							'&:before': { borderBottom: 'none' },
							'&:after': { borderBottom: 'none' },
							'&:hover:not(.Mui-disabled):before': {
								borderBottom: 'none',
							},
						},
						px: 1,
						borderRight: '2px solid #000',
					}}
				/>

				{/* Регион */}
				<Box
					sx={{
						display: 'flex',
						flexDirection: 'column',
						alignItems: 'center',
						px: 1.5,
						py: 1,
						backgroundColor: '#fff',
						minWidth: '70px',
					}}
				>
					<TextField
						value={plateData.region || ''}
						onChange={(e) => {
							const filtered = filterDigits(e.target.value)
							if (filtered.length > 0 && filtered.replace(/0/g, '').length === 0) {
								return
							}
							const region = filtered.slice(0, 3)
							updatePlateData({ region })
						}}
						variant="standard"
						inputProps={{
							style: {
								fontSize: '16px',
								fontWeight: 'bold',
								textAlign: 'center',
								width: '40px',
								border: 'none',
								outline: 'none',
							},
							placeholder: '77',
							maxLength: 3,
						}}
						sx={{
							'& .MuiInput-root': {
								'&:before': { borderBottom: 'none' },
								'&:after': { borderBottom: 'none' },
								'&:hover:not(.Mui-disabled):before': {
									borderBottom: 'none',
								},
							},
						}}
					/>
					<Box
						sx={{
							display: 'flex',
							alignItems: 'center',
							fontSize: '10px',
							fontWeight: 'bold',
							color: '#000',
						}}
					>
						RUS
						<Box
							sx={{
								ml: 0.5,
								width: '12px',
								height: '8px',
								background:
									'linear-gradient(to bottom, #fff 0%, #fff 33%, #0052cc 33%, #0052cc 66%, #d32f2f 66%, #d32f2f 100%)',
								border: '0.5px solid #000',
							}}
						/>
					</Box>
				</Box>
			</Paper>
			<Typography variant="body2" color="text.secondary">
				Пример: ММ9768 77
			</Typography>
		</Box>
	)

	// Рендеринг формы для тракторов и мотоциклов (двухстрочный формат)
	const renderTwoLineForm = () => (
		<Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', alignItems: 'center' }}>
			<Paper
				elevation={1}
				sx={{
					display: 'flex',
					flexDirection: 'column',
					border: error ? '2px solid #d32f2f' : '2px solid #000',
					borderRadius: '8px',
					overflow: 'hidden',
					backgroundColor: '#fff',
					minWidth: '120px',
				}}
			>
				{/* Верхняя строка - четыре цифры */}
				<Box
					sx={{
						display: 'flex',
						justifyContent: 'center',
						borderBottom: '1px solid #000',
						py: 0.5,
					}}
				>
					<TextField
						value={plateData.topDigits || ''}
						onChange={(e) => {
							const filtered = filterDigits(e.target.value).slice(0, 4)
							updatePlateData({ topDigits: filtered })
						}}
						variant="standard"
						inputProps={{
							style: {
								fontSize: '20px',
								fontWeight: 'bold',
								textAlign: 'center',
								width: '80px',
								border: 'none',
								outline: 'none',
							},
							placeholder: '1234',
							maxLength: 4,
						}}
						sx={{
							'& .MuiInput-root': {
								'&:before': { borderBottom: 'none' },
								'&:after': { borderBottom: 'none' },
								'&:hover:not(.Mui-disabled):before': {
									borderBottom: 'none',
								},
							},
						}}
					/>
				</Box>

				{/* Нижняя строка - две буквы и регион */}
				<Box
					sx={{
						display: 'flex',
						alignItems: 'center',
						justifyContent: 'space-between',
						px: 1,
						py: 0.5,
					}}
				>
					<TextField
						value={plateData.bottomLetters || ''}
						onChange={(e) => {
							const filtered = filterLetters(e.target.value).slice(0, 2)
							updatePlateData({ bottomLetters: filtered })
						}}
						variant="standard"
						inputProps={{
							style: {
								fontSize: '16px',
								fontWeight: 'bold',
								textAlign: 'center',
								width: '40px',
								border: 'none',
								outline: 'none',
							},
							placeholder: 'АБ',
							maxLength: 2,
						}}
						sx={{
							'& .MuiInput-root': {
								'&:before': { borderBottom: 'none' },
								'&:after': { borderBottom: 'none' },
								'&:hover:not(.Mui-disabled):before': {
									borderBottom: 'none',
								},
							},
						}}
					/>

					<TextField
						value={plateData.bottomRegion || ''}
						onChange={(e) => {
							const filtered = filterDigits(e.target.value)
							if (filtered.length > 0 && filtered.replace(/0/g, '').length === 0) {
								return
							}
							const region = filtered.slice(0, 3)
							updatePlateData({ bottomRegion: region })
						}}
						variant="standard"
						inputProps={{
							style: {
								fontSize: '16px',
								fontWeight: 'bold',
								textAlign: 'center',
								width: '40px',
								border: 'none',
								outline: 'none',
							},
							placeholder: '77',
							maxLength: 3,
						}}
						sx={{
							'& .MuiInput-root': {
								'&:before': { borderBottom: 'none' },
								'&:after': { borderBottom: 'none' },
								'&:hover:not(.Mui-disabled):before': {
									borderBottom: 'none',
								},
							},
						}}
					/>
				</Box>
			</Paper>
			<Typography variant="body2" color="text.secondary">
				{plateData.category === 'tractor' ? 'Пример: 1234 АБ 77' : 'Пример: 5678 ВГ 77'}
			</Typography>
		</Box>
	)

	// Получение текущего номера для отображения
	const currentPlateNumber = generatePlateNumber(plateData)

	return (
		<Box>
			<Typography
				variant="body2"
				color="text.secondary"
				gutterBottom
				sx={{ mb: 1 }}
			>
				Регистрационный знак {required && '*'}
			</Typography>

			{/* Выбор категории */}
			<FormControl fullWidth sx={{ mb: 2 }}>
				<InputLabel>Категория транспортного средства</InputLabel>
				<Select
					value={plateData.category}
					onChange={(e) => handleCategoryChange(e.target.value as VehicleCategory)}
					label="Категория транспортного средства"
				>
					{VEHICLE_CATEGORIES.map((category) => (
						<MenuItem key={category.value} value={category.value}>
							{category.label}
						</MenuItem>
					))}
				</Select>
			</FormControl>

			{/* Форма ввода в зависимости от категории */}
			<Box sx={{ mb: 2 }}>
				{plateData.category === 'standard' && renderStandardForm()}
				{plateData.category === 'trailer' && renderTrailerForm()}
				{(plateData.category === 'tractor' || plateData.category === 'motorcycle') &&
					renderTwoLineForm()}
			</Box>

			{/* Отображение ошибки */}
			{error && (
				<Typography variant="caption" color="error" sx={{ display: 'block', mb: 1 }}>
					{error}
				</Typography>
			)}

			{/* Подсказка */}
			<Typography variant="caption" color="text.secondary" sx={{ display: 'block' }}>
				Разрешенные буквы: {ALLOWED_LETTERS}
				{currentPlateNumber && (
					<>
						<br />
						Итоговый номер: <strong>{currentPlateNumber}</strong>
					</>
				)}
			</Typography>
		</Box>
	)
}

export default LicensePlateInput
