import React, { useState, useCallback } from "react"
import {
	Container,
	Paper,
	Typography,
	TextField,
	Button,
	Grid,
	Alert,
	CircularProgress,
	Box,
	FormControl,
	InputLabel,
	Select,
	MenuItem,
} from "@mui/material"

import {
	Download as DownloadIcon,
	Description as PdfIcon,
} from "@mui/icons-material"
import { PolicyData, FormState } from "../types"
import { PdfGeneratorApi, downloadFile } from "../services/api"

// Начальные данные формы
const initialData: PolicyData = {
	fio: "",
	address: "",
	date_start: "",
	date_end: "",
	reg_number: "",
	vehicle_type: "",
	brand_model: "",
}

// Варианты типов транспортных средств
const vehicleTypes = [
	{ value: "B", label: "B - Легковые автомобили" },
	{ value: "C", label: "C - Грузовые автомобили" },
	{ value: "D", label: "D - Автобусы" },
	{ value: "A", label: "A - Мотоциклы" },
	{ value: "BE", label: "BE - Легковые с прицепом" },
	{ value: "CE", label: "CE - Грузовые с прицепом" },
]

const PolicyForm: React.FC = () => {
	const [formState, setFormState] = useState<FormState>({
		data: initialData,
		loading: false,
		errors: {},
	})

	const [successMessage, setSuccessMessage] = useState<string>("")

	// Обновление данных формы
	const updateFormData = useCallback(
		(field: keyof PolicyData, value: string | number) => {
			setFormState((prev) => ({
				...prev,
				data: { ...prev.data, [field]: value },
				errors: { ...prev.errors, [field]: "" }, // Очищаем ошибку при изменении
			}))
			setSuccessMessage("") // Очищаем сообщение об успехе
		},
		[]
	)

	// Валидация формы
	const validateForm = useCallback((): boolean => {
		const { data } = formState
		const newErrors: Partial<Record<keyof PolicyData, string>> = {}

		// Проверяем обязательные поля
		if (!data.fio.trim()) newErrors.fio = "ФИО обязательно для заполнения"
		if (!data.address.trim())
			newErrors.address = "Адрес обязателен для заполнения"
		if (!data.reg_number.trim())
			newErrors.reg_number = "Регистрационный знак обязателен"
		if (!data.vehicle_type.trim()) newErrors.vehicle_type = "Тип ТС обязателен"
		if (!data.brand_model.trim())
			newErrors.brand_model = "Марка и модель обязательны"

		// Проверяем формат дат
		const dateRegex = /^\d{2}\.\d{2}\.\d{4}$/
		if (!data.date_start.trim()) {
			newErrors.date_start = "Дата начала обязательна"
		} else if (!dateRegex.test(data.date_start)) {
			newErrors.date_start = "Формат даты: ДД.ММ.ГГГГ"
		}

		if (!data.date_end.trim()) {
			newErrors.date_end = "Дата окончания обязательна"
		} else if (!dateRegex.test(data.date_end)) {
			newErrors.date_end = "Формат даты: ДД.ММ.ГГГГ"
		}

		setFormState((prev) => ({ ...prev, errors: newErrors }))
		return Object.keys(newErrors).length === 0
	}, [formState])

	// Отправка формы
	const handleSubmit = useCallback(
		async (e: React.FormEvent) => {
			e.preventDefault()

			if (!validateForm()) {
				return
			}

			setFormState((prev) => ({ ...prev, loading: true }))
			setSuccessMessage("")

			try {
				const response = await PdfGeneratorApi.generatePdf(formState.data)
				downloadFile(response.blob, response.filename)
				setSuccessMessage(
					`PDF файл "${response.filename}" успешно сгенерирован и загружен!`
				)
			} catch (error: any) {
				setFormState((prev) => ({
					...prev,
					errors: { ...prev.errors, fio: error.message },
				}))
			} finally {
				setFormState((prev) => ({ ...prev, loading: false }))
			}
		},
		[formState.data, validateForm]
	)

	// Сброс формы
	const handleReset = useCallback(() => {
		setFormState({
			data: initialData,
			loading: false,
			errors: {},
		})
		setSuccessMessage("")
	}, [])

	const { data, loading, errors } = formState

	return (
		<Container
			maxWidth="md"
			sx={{ py: 4 }}
		>
			<Paper
				elevation={3}
				sx={{ p: 4 }}
			>
				<Box
					display="flex"
					alignItems="center"
					mb={3}
				>
					<PdfIcon sx={{ mr: 2, fontSize: 40, color: "primary.main" }} />
					<Typography
						variant="h4"
						component="h1"
						color="primary"
					>
						Генератор PDF полисов
					</Typography>
				</Box>

				{successMessage && (
					<Alert
						severity="success"
						sx={{ mb: 3 }}
					>
						{successMessage}
					</Alert>
				)}

				<form onSubmit={handleSubmit}>
					<Grid
						container
						spacing={3}
					>
						{/* ФИО */}
						<Grid
							item
							xs={12}
						>
							<TextField
								fullWidth
								label="ФИО страхователя"
								value={data.fio}
								onChange={(e) => updateFormData("fio", e.target.value)}
								error={!!errors.fio}
								helperText={errors.fio}
								placeholder="Иванов Иван Иванович"
								required
							/>
						</Grid>

						{/* Адрес */}
						<Grid
							item
							xs={12}
						>
							<TextField
								fullWidth
								label="Адрес страхователя"
								value={data.address}
								onChange={(e) => updateFormData("address", e.target.value)}
								error={!!errors.address}
								helperText={errors.address}
								placeholder="г. Москва, ул. Ленина, д. 1, кв. 1"
								required
							/>
						</Grid>

						{/* Даты */}
						<Grid
							item
							xs={12}
							md={6}
						>
							<LocalizationProvider
								dateAdapter={AdapterDateFns}
								adapterLocale={ru}
							>
								<DatePicker
									label="Дата начала полиса"
									value={
										data.date_start
											? new Date(data.date_start.split(".").reverse().join("-"))
											: null
									}
									onChange={(newValue) => {
										if (newValue) {
											const day = String(newValue.getDate()).padStart(2, "0")
											const month = String(newValue.getMonth() + 1).padStart(
												2,
												"0"
											)
											const year = newValue.getFullYear()
											updateFormData("date_start", `${day}.${month}.${year}`)
										} else {
											updateFormData("date_start", "")
										}
									}}
									slotProps={{
										textField: {
											fullWidth: true,
											error: !!errors.date_start,
											helperText: errors.date_start,
											required: true,
										},
									}}
								/>
							</LocalizationProvider>
						</Grid>

						<Grid
							item
							xs={12}
							md={6}
						>
							<LocalizationProvider
								dateAdapter={AdapterDateFns}
								adapterLocale={ru}
							>
								<DatePicker
									label="Дата окончания полиса"
									value={
										data.date_end
											? new Date(data.date_end.split(".").reverse().join("-"))
											: null
									}
									onChange={(newValue) => {
										if (newValue) {
											const day = String(newValue.getDate()).padStart(2, "0")
											const month = String(newValue.getMonth() + 1).padStart(
												2,
												"0"
											)
											const year = newValue.getFullYear()
											updateFormData("date_end", `${day}.${month}.${year}`)
										} else {
											updateFormData("date_end", "")
										}
									}}
									slotProps={{
										textField: {
											fullWidth: true,
											error: !!errors.date_end,
											helperText: errors.date_end,
											required: true,
										},
									}}
								/>
							</LocalizationProvider>
						</Grid>

						{/* Регистрационный знак */}
						<Grid
							item
							xs={12}
							md={6}
						>
							<TextField
								fullWidth
								label="Регистрационный знак"
								value={data.reg_number}
								onChange={(e) =>
									updateFormData("reg_number", e.target.value.toUpperCase())
								}
								error={!!errors.reg_number}
								helperText={errors.reg_number}
								placeholder="А123БВ77"
								required
							/>
						</Grid>

						{/* Тип ТС */}
						<Grid
							item
							xs={12}
							md={6}
						>
							<FormControl
								fullWidth
								error={!!errors.vehicle_type}
								required
							>
								<InputLabel>Тип транспортного средства</InputLabel>
								<Select
									value={data.vehicle_type}
									onChange={(e) =>
										updateFormData("vehicle_type", e.target.value)
									}
									label="Тип транспортного средства"
								>
									{vehicleTypes.map((type) => (
										<MenuItem
											key={type.value}
											value={type.value}
										>
											{type.label}
										</MenuItem>
									))}
								</Select>
								{errors.vehicle_type && (
									<Typography
										variant="caption"
										color="error"
										sx={{ mt: 0.5, mx: 1.75 }}
									>
										{errors.vehicle_type}
									</Typography>
								)}
							</FormControl>
						</Grid>

						{/* Марка и модель */}
						<Grid
							item
							xs={12}
						>
							<TextField
								fullWidth
								label="Марка и модель"
								value={data.brand_model}
								onChange={(e) => updateFormData("brand_model", e.target.value)}
								error={!!errors.brand_model}
								helperText={errors.brand_model}
								placeholder="Toyota Camry"
								required
							/>
						</Grid>



						{/* Кнопки */}
						<Grid
							item
							xs={12}
						>
							<Box
								display="flex"
								gap={2}
								justifyContent="flex-end"
							>
								<Button
									type="button"
									variant="outlined"
									onClick={handleReset}
									disabled={loading}
								>
									Очистить
								</Button>
								<Button
									type="submit"
									variant="contained"
									startIcon={
										loading ? <CircularProgress size={20} /> : <DownloadIcon />
									}
									disabled={loading}
									size="large"
								>
									{loading ? "Генерация PDF..." : "Скачать PDF"}
								</Button>
							</Box>
						</Grid>
					</Grid>
				</form>
			</Paper>
		</Container>
	)
}

export default PolicyForm
