import React, { useState, useCallback } from "react"
import {
	Container,
	Paper,
	Typography,
	TextField,
	Button,
	Grid,
	Alert,
	CircularProgress,
	Box,
	FormControl,
	InputLabel,
	Select,
	MenuItem,
	Autocomplete,
} from "@mui/material"

import {
	Download as DownloadIcon,
	Description as PdfIcon,
} from "@mui/icons-material"
import { PolicyData, FormState } from "../types"
import { PdfGeneratorApi, downloadFile } from "../services/api"

// –ù–∞—á–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —Ñ–æ—Ä–º—ã
const initialData: PolicyData = {
	fio: "",
	address: "",
	date_start: "",
	date_end: "",
	reg_number: "",
	vehicle_type: "",
	brand_model: "",
}

// –í–∞—Ä–∏–∞–Ω—Ç—ã —Ç–∏–ø–æ–≤ —Ç—Ä–∞–Ω—Å–ø–æ—Ä—Ç–Ω—ã—Ö —Å—Ä–µ–¥—Å—Ç–≤
const vehicleTypes = [
	{ value: "B", label: "B - –õ–µ–≥–∫–æ–≤—ã–µ –∞–≤—Ç–æ–º–æ–±–∏–ª–∏" },
	{ value: "C", label: "C - –ì—Ä—É–∑–æ–≤—ã–µ –∞–≤—Ç–æ–º–æ–±–∏–ª–∏" },
	{ value: "D", label: "D - –ê–≤—Ç–æ–±—É—Å—ã" },
	{ value: "A", label: "A - –ú–æ—Ç–æ—Ü–∏–∫–ª—ã" },
	{ value: "BE", label: "BE - –õ–µ–≥–∫–æ–≤—ã–µ —Å –ø—Ä–∏—Ü–µ–ø–æ–º" },
	{ value: "CE", label: "CE - –ì—Ä—É–∑–æ–≤—ã–µ —Å –ø—Ä–∏—Ü–µ–ø–æ–º" },
]

const PolicyForm: React.FC = () => {
	const [formState, setFormState] = useState<FormState>({
		data: initialData,
		loading: false,
		errors: {},
	})

	const [successMessage, setSuccessMessage] = useState<string>("")
	const [vehicleOptions, setVehicleOptions] = useState<
		Array<{ label: string; value: string }>
	>([])
	const [vehicleLoading, setVehicleLoading] = useState<boolean>(false)

	// –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö —Ñ–æ—Ä–º—ã
	const updateFormData = useCallback(
		(field: keyof PolicyData, value: string | number) => {
			setFormState((prev) => ({
				...prev,
				data: { ...prev.data, [field]: value },
				errors: { ...prev.errors, [field]: "" }, // –û—á–∏—â–∞–µ–º –æ—à–∏–±–∫—É –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏
			}))
			setSuccessMessage("") // –û—á–∏—â–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± —É—Å–ø–µ—Ö–µ
		},
		[]
	)

	// –í–∞–ª–∏–¥–∞—Ü–∏—è —Ñ–æ—Ä–º—ã
	const validateForm = useCallback((): boolean => {
		const { data } = formState
		const newErrors: Partial<Record<keyof PolicyData, string>> = {}

		// –ü—Ä–æ–≤–µ—Ä—è–µ–º –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è
		if (!data.fio.trim()) newErrors.fio = "–§–ò–û –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ –¥–ª—è –∑–∞–ø–æ–ª–Ω–µ–Ω–∏—è"
		if (!data.address.trim())
			newErrors.address = "–ê–¥—Ä–µ—Å –æ–±—è–∑–∞—Ç–µ–ª–µ–Ω –¥–ª—è –∑–∞–ø–æ–ª–Ω–µ–Ω–∏—è"
		if (!data.reg_number.trim())
			newErrors.reg_number = "–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–æ–Ω–Ω—ã–π –∑–Ω–∞–∫ –æ–±—è–∑–∞—Ç–µ–ª–µ–Ω"
		if (!data.vehicle_type.trim()) newErrors.vehicle_type = "–¢–∏–ø –¢–° –æ–±—è–∑–∞—Ç–µ–ª–µ–Ω"
		if (!data.brand_model.trim())
			newErrors.brand_model = "–ú–∞—Ä–∫–∞ –∏ –º–æ–¥–µ–ª—å –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã"

		// –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ñ–æ—Ä–º–∞—Ç –¥–∞—Ç
		const dateRegex = /^\d{2}\.\d{2}\.\d{4}$/
		if (!data.date_start.trim()) {
			newErrors.date_start = "–î–∞—Ç–∞ –Ω–∞—á–∞–ª–∞ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–∞"
		} else if (!dateRegex.test(data.date_start)) {
			newErrors.date_start = "–§–æ—Ä–º–∞—Ç –¥–∞—Ç—ã: –î–î.–ú–ú.–ì–ì–ì–ì"
		}

		if (!data.date_end.trim()) {
			newErrors.date_end = "–î–∞—Ç–∞ –æ–∫–æ–Ω—á–∞–Ω–∏—è –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–∞"
		} else if (!dateRegex.test(data.date_end)) {
			newErrors.date_end = "–§–æ—Ä–º–∞—Ç –¥–∞—Ç—ã: –î–î.–ú–ú.–ì–ì–ì–ì"
		}

		setFormState((prev) => ({ ...prev, errors: newErrors }))
		return Object.keys(newErrors).length === 0
	}, [formState])

	// –û—Ç–ø—Ä–∞–≤–∫–∞ —Ñ–æ—Ä–º—ã
	const handleSubmit = useCallback(
		async (e: React.FormEvent) => {
			e.preventDefault()

			if (!validateForm()) {
				return
			}

			setFormState((prev) => ({ ...prev, loading: true }))
			setSuccessMessage("")

			try {
				const response = await PdfGeneratorApi.generatePdf(formState.data)
				downloadFile(response.blob, response.filename)
				setSuccessMessage(
					`PDF —Ñ–∞–π–ª "${response.filename}" —É—Å–ø–µ—à–Ω–æ —Å–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞–Ω –∏ –∑–∞–≥—Ä—É–∂–µ–Ω!`
				)
			} catch (error: any) {
				setFormState((prev) => ({
					...prev,
					errors: { ...prev.errors, fio: error.message },
				}))
			} finally {
				setFormState((prev) => ({ ...prev, loading: false }))
			}
		},
		[formState.data, validateForm]
	)

	// –ü–æ–∏—Å–∫ –∞–≤—Ç–æ–º–æ–±–∏–ª–µ–π
	const searchVehicles = useCallback(async (query: string) => {
		if (!query || query.length < 2) {
			setVehicleOptions([])
			return
		}

		setVehicleLoading(true)
		try {
			const response = await fetch(
				`/api/search-vehicles?q=${encodeURIComponent(query)}`
			)
			const data = await response.json()

			if (data.results) {
				const options = data.results.map((item: any) => ({
					label: item.label,
					value: item.value,
				}))
				setVehicleOptions(options)
			}
		} catch (error) {
			console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–∏—Å–∫–µ –∞–≤—Ç–æ–º–æ–±–∏–ª–µ–π:", error)
			setVehicleOptions([])
		} finally {
			setVehicleLoading(false)
		}
	}, [])

	// –°–±—Ä–æ—Å —Ñ–æ—Ä–º—ã
	const handleReset = useCallback(() => {
		setFormState({
			data: initialData,
			loading: false,
			errors: {},
		})
		setSuccessMessage("")
		setVehicleOptions([])
	}, [])

	const { data, loading, errors } = formState

	return (
		<Container
			maxWidth="md"
			sx={{ py: 4 }}
		>
			<Paper
				elevation={3}
				sx={{ p: 4 }}
			>
				<Box
					display="flex"
					alignItems="center"
					mb={3}
				>
					<PdfIcon sx={{ mr: 2, fontSize: 40, color: "primary.main" }} />
					<Typography
						variant="h4"
						component="h1"
						color="primary"
					>
						–ì–µ–Ω–µ—Ä–∞—Ç–æ—Ä PDF –ø–æ–ª–∏—Å–æ–≤
					</Typography>
				</Box>

				{successMessage && (
					<Alert
						severity="success"
						sx={{ mb: 3 }}
					>
						{successMessage}
					</Alert>
				)}

				<form onSubmit={handleSubmit}>
					<Grid
						container
						spacing={3}
					>
						{/* –§–ò–û */}
						<Grid
							item
							xs={12}
						>
							<TextField
								fullWidth
								label="–§–ò–û —Å—Ç—Ä–∞—Ö–æ–≤–∞—Ç–µ–ª—è"
								value={data.fio}
								onChange={(e) => updateFormData("fio", e.target.value)}
								error={!!errors.fio}
								helperText={errors.fio}
								placeholder="–ò–≤–∞–Ω–æ–≤ –ò–≤–∞–Ω –ò–≤–∞–Ω–æ–≤–∏—á"
								required
							/>
						</Grid>

						{/* –ê–¥—Ä–µ—Å */}
						<Grid
							item
							xs={12}
						>
							<TextField
								fullWidth
								label="–ê–¥—Ä–µ—Å —Å—Ç—Ä–∞—Ö–æ–≤–∞—Ç–µ–ª—è"
								value={data.address}
								onChange={(e) => updateFormData("address", e.target.value)}
								error={!!errors.address}
								helperText={errors.address}
								placeholder="–≥. –ú–æ—Å–∫–≤–∞, —É–ª. –õ–µ–Ω–∏–Ω–∞, –¥. 1, –∫–≤. 1"
								required
							/>
						</Grid>

						{/* –î–∞—Ç—ã */}
						<Grid
							item
							xs={12}
							md={6}
						>
							<TextField
								fullWidth
								type="date"
								label="–î–∞—Ç–∞ –Ω–∞—á–∞–ª–∞ –ø–æ–ª–∏—Å–∞"
								value={
									data.date_start
										? data.date_start.split(".").reverse().join("-")
										: ""
								}
								onChange={(e) => {
									if (e.target.value) {
										const [year, month, day] = e.target.value.split("-")
										updateFormData("date_start", `${day}.${month}.${year}`)
									} else {
										updateFormData("date_start", "")
									}
								}}
								error={!!errors.date_start}
								helperText={errors.date_start || "–í—ã–±–µ—Ä–∏—Ç–µ –¥–∞—Ç—É"}
								required
								InputLabelProps={{
									shrink: true,
								}}
							/>
						</Grid>

						<Grid
							item
							xs={12}
							md={6}
						>
							<TextField
								fullWidth
								type="date"
								label="–î–∞—Ç–∞ –æ–∫–æ–Ω—á–∞–Ω–∏—è –ø–æ–ª–∏—Å–∞"
								value={
									data.date_end
										? data.date_end.split(".").reverse().join("-")
										: ""
								}
								onChange={(e) => {
									if (e.target.value) {
										const [year, month, day] = e.target.value.split("-")
										updateFormData("date_end", `${day}.${month}.${year}`)
									} else {
										updateFormData("date_end", "")
									}
								}}
								error={!!errors.date_end}
								helperText={errors.date_end || "–í—ã–±–µ—Ä–∏—Ç–µ –¥–∞—Ç—É"}
								required
								InputLabelProps={{
									shrink: true,
								}}
							/>
						</Grid>

						{/* –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–æ–Ω–Ω—ã–π –∑–Ω–∞–∫ */}
						<Grid
							item
							xs={12}
							md={6}
						>
							<Box>
								<Typography
									variant="body2"
									color="text.secondary"
									gutterBottom
									sx={{ mb: 1 }}
								>
									–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–æ–Ω–Ω—ã–π –∑–Ω–∞–∫ *
								</Typography>
								<Box
									sx={{
										display: "flex",
										gap: 1,
										alignItems: "center",
										flexWrap: "wrap",
										p: 2,
										border: errors.reg_number ? "2px solid #d32f2f" : "1px solid #ccc",
										borderRadius: 1,
										backgroundColor: "#fff",
										"&:focus-within": {
											borderColor: errors.reg_number ? "#d32f2f" : "#1976d2",
											borderWidth: "2px",
										},
									}}
								>
									{/* –ü–µ—Ä–≤–∞—è –±—É–∫–≤–∞ */}
									<TextField
										value={data.reg_number.charAt(0) || ""}
										onChange={(e) => {
											const newValue = e.target.value.toUpperCase().slice(0, 1)
											const currentNumber = data.reg_number.split("")
											currentNumber[0] = newValue
											updateFormData("reg_number", currentNumber.join("").slice(0, 9))
										}}
										variant="outlined"
										size="small"
										inputProps={{
											maxLength: 1,
											style: {
												textAlign: "center",
												width: "20px",
												fontSize: "18px",
												fontWeight: "bold",
											},
										}}
										sx={{
											width: "40px",
											"& .MuiOutlinedInput-root": {
												height: "40px",
											},
										}}
									/>

									{/* –¶–∏—Ñ—Ä—ã */}
									{[1, 2, 3].map((index) => (
										<TextField
											key={index}
											value={data.reg_number.charAt(index) || ""}
											onChange={(e) => {
												const newValue = e.target.value.replace(/\D/g, "").slice(0, 1)
												const currentNumber = data.reg_number.split("")
												currentNumber[index] = newValue
												updateFormData("reg_number", currentNumber.join("").slice(0, 9))
											}}
											variant="outlined"
											size="small"
											inputProps={{
												maxLength: 1,
												style: {
													textAlign: "center",
													width: "20px",
													fontSize: "18px",
													fontWeight: "bold",
												},
											}}
											sx={{
												width: "40px",
												"& .MuiOutlinedInput-root": {
													height: "40px",
												},
											}}
										/>
									))}

									{/* –î–≤–µ –±—É–∫–≤—ã */}
									{[4, 5].map((index) => (
										<TextField
											key={index}
											value={data.reg_number.charAt(index) || ""}
											onChange={(e) => {
												const newValue = e.target.value.toUpperCase().slice(0, 1)
												const currentNumber = data.reg_number.split("")
												currentNumber[index] = newValue
												updateFormData("reg_number", currentNumber.join("").slice(0, 9))
											}}
											variant="outlined"
											size="small"
											inputProps={{
												maxLength: 1,
												style: {
													textAlign: "center",
													width: "20px",
													fontSize: "18px",
													fontWeight: "bold",
												},
											}}
											sx={{
												width: "40px",
												"& .MuiOutlinedInput-root": {
													height: "40px",
												},
											}}
										/>
									))}

									{/* –†–µ–≥–∏–æ–Ω */}
									{[6, 7, 8].map((index) => (
										<TextField
											key={index}
											value={data.reg_number.charAt(index) || ""}
											onChange={(e) => {
												const newValue = e.target.value.replace(/\D/g, "").slice(0, 1)
												const currentNumber = data.reg_number.split("")
												currentNumber[index] = newValue
												updateFormData("reg_number", currentNumber.join("").slice(0, 9))
											}}
											variant="outlined"
											size="small"
											inputProps={{
												maxLength: 1,
												style: {
													textAlign: "center",
													width: "20px",
													fontSize: "18px",
													fontWeight: "bold",
												},
											}}
											sx={{
												width: "40px",
												"& .MuiOutlinedInput-root": {
													height: "40px",
												},
											}}
										/>
									))}

									{/* –§–ª–∞–≥ RUS */}
									<Box
										sx={{
											ml: 1,
											display: "flex",
											alignItems: "center",
											fontSize: "12px",
											fontWeight: "bold",
											color: "#666",
										}}
									>
										RUS üá∑üá∫
									</Box>
								</Box>
								{errors.reg_number && (
									<Typography
										variant="caption"
										color="error"
										sx={{ mt: 0.5, display: "block" }}
									>
										{errors.reg_number}
									</Typography>
								)}
								<Typography
									variant="caption"
									color="text.secondary"
									sx={{ mt: 0.5, display: "block" }}
								>
									–§–æ—Ä–º–∞—Ç: –ê123–ë–í77 (1 –±—É–∫–≤–∞, 3 —Ü–∏—Ñ—Ä—ã, 2 –±—É–∫–≤—ã, 2-3 —Ü–∏—Ñ—Ä—ã —Ä–µ–≥–∏–æ–Ω–∞)
								</Typography>
							</Box>
						</Grid>

						{/* –¢–∏–ø –¢–° */}
						<Grid
							item
							xs={12}
							md={6}
						>
							<FormControl
								fullWidth
								error={!!errors.vehicle_type}
								required
							>
								<InputLabel>–¢–∏–ø —Ç—Ä–∞–Ω—Å–ø–æ—Ä—Ç–Ω–æ–≥–æ —Å—Ä–µ–¥—Å—Ç–≤–∞</InputLabel>
								<Select
									value={data.vehicle_type}
									onChange={(e) =>
										updateFormData("vehicle_type", e.target.value)
									}
									label="–¢–∏–ø —Ç—Ä–∞–Ω—Å–ø–æ—Ä—Ç–Ω–æ–≥–æ —Å—Ä–µ–¥—Å—Ç–≤–∞"
								>
									{vehicleTypes.map((type) => (
										<MenuItem
											key={type.value}
											value={type.value}
										>
											{type.label}
										</MenuItem>
									))}
								</Select>
								{errors.vehicle_type && (
									<Typography
										variant="caption"
										color="error"
										sx={{ mt: 0.5, mx: 1.75 }}
									>
										{errors.vehicle_type}
									</Typography>
								)}
							</FormControl>
						</Grid>

						{/* –ú–∞—Ä–∫–∞ –∏ –º–æ–¥–µ–ª—å */}
						<Grid
							item
							xs={12}
						>
							<Autocomplete
								freeSolo
								options={vehicleOptions}
								getOptionLabel={(option) =>
									typeof option === "string" ? option : option.label
								}
								value={data.brand_model}
								onInputChange={(event, newInputValue) => {
									updateFormData("brand_model", newInputValue)
									searchVehicles(newInputValue)
								}}
								onChange={(event, newValue) => {
									const value =
										typeof newValue === "string"
											? newValue
											: newValue?.value || ""
									updateFormData("brand_model", value)
								}}
								loading={vehicleLoading}
								renderInput={(params) => (
									<TextField
										{...params}
										fullWidth
										label="–ú–∞—Ä–∫–∞ –∏ –º–æ–¥–µ–ª—å"
										placeholder="–ù–∞—á–Ω–∏—Ç–µ –≤–≤–æ–¥–∏—Ç—å –º–∞—Ä–∫—É –∏–ª–∏ –º–æ–¥–µ–ª—å..."
										error={!!errors.brand_model}
										helperText={
											errors.brand_model ||
											"–í—ã–±–µ—Ä–∏—Ç–µ –∏–∑ —Å–ø–∏—Å–∫–∞ –∏–ª–∏ –≤–≤–µ–¥–∏—Ç–µ —Å–≤–æ–π –≤–∞—Ä–∏–∞–Ω—Ç"
										}
										required
										InputProps={{
											...params.InputProps,
											endAdornment: (
												<>
													{vehicleLoading ? (
														<CircularProgress
															color="inherit"
															size={20}
														/>
													) : null}
													{params.InputProps.endAdornment}
												</>
											),
										}}
									/>
								)}
								renderOption={(props, option) => (
									<li {...props}>
										{typeof option === "string" ? option : option.label}
									</li>
								)}
								noOptionsText="–ù–µ—Ç –ø–æ–¥—Ö–æ–¥—è—â–∏—Ö –≤–∞—Ä–∏–∞–Ω—Ç–æ–≤"
								loadingText="–ü–æ–∏—Å–∫..."
							/>
						</Grid>

						{/* –ö–Ω–æ–ø–∫–∏ */}
						<Grid
							item
							xs={12}
						>
							<Box
								display="flex"
								gap={2}
								justifyContent="flex-end"
							>
								<Button
									type="button"
									variant="outlined"
									onClick={handleReset}
									disabled={loading}
								>
									–û—á–∏—Å—Ç–∏—Ç—å
								</Button>
								<Button
									type="submit"
									variant="contained"
									startIcon={
										loading ? <CircularProgress size={20} /> : <DownloadIcon />
									}
									disabled={loading}
									size="large"
								>
									{loading ? "–ì–µ–Ω–µ—Ä–∞—Ü–∏—è PDF..." : "–°–∫–∞—á–∞—Ç—å PDF"}
								</Button>
							</Box>
						</Grid>
					</Grid>
				</form>
			</Paper>
		</Container>
	)
}

export default PolicyForm
